import * as React from 'react';
import styled from "@emotion/styled"
import Add from '@mui/icons-material/Add';
import Remove from '@mui/icons-material/Remove';
import {TimeSlider} from './TimeSlider';



const DayStyle = styled.div`
display: flex;
`;

const Name = styled.div`width: 50px;`;
const Check = styled.div`width: 50px;`;
const Data = styled.div`width: 300px;
position: relative;
> * {
  position: absolute;
}
`;
const Action = styled.div`width: 50px;`;

function getSlider() { 
    return  <TimeSlider 
   min={6}
   max={24}
   defaultValue={6}
   step={6}
   label="one" 
   marks={[{value: 6, label: '6'}, {value: 12, label: '12'}, {value: 18, label: '18'}, {value: 24, label: '24'}]}/>;
}

export function Day({day}) {
    const [value, setValue] = React.useState(false);
    return <DayStyle>
        <Name>{day}</Name>
        <Check><input type='checkbox'/></Check>
        <Data>{value? [getSlider(), getSlider()]: getSlider()}</Data>
        <Action>{<a href="#" onClick={() => setValue(!value)}>{value? <Remove/>: <Add/>}</a>}</Action>
    </DayStyle>
}
